// TopicPage.js
import React from 'react';
import { useParams } from 'react-router-dom';

function TopicPage({ topic, topicData }) {
  const { topicid } = useParams();

  if (!topic) return <div>Topic not found</div>;

  return (
    <div>
      <h2>{topic.topicid}</h2>
      {topic.sections.map((section, idx) => (
        <div key={idx}>
          <h3>{section.title}</h3>
          <ul>
            {section.qa.map((qa, i) => (
              <li key={i}>
                {qa.answerArr.map((answer, j) => (
                  <div key={j}>
                    {answer.type === 'url' ? (
                      <span dangerouslySetInnerHTML={{ __html: answer.key }} />
                    ) : answer.type === 'youtube' ? (
                      <div>
                        <p>{answer.name}</p>
                        <iframe
                          width="560"
                          height="315"
                          src={`https://www.youtube.com/embed/${answer.videoId}`}
                          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                          allowFullScreen
                        ></iframe>
                      </div>
                    ) : null}
                  </div>
                ))}
              </li>
            ))}
          </ul>
        </div>
      ))}
      {/* Render list of topics */}
      <div>
        <h3>All Topics:</h3>
        <ul>
          {topicData.map((topic, index) => (
            <li key={index}><a href={`/${topic.topicid}`}>{topic.topicid}</a></li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default TopicPage;
