import React from 'react';
import { useParams } from 'react-router-dom';
import ResourceNav from '../../Components/ResourceNav';
import topicData from '../../data/topicData.json';

function TopicPage() {
  const { topicid } = useParams();
  const topic = topicData.filter(topic => topic.topicid === topicid)[0]; // Filter topics array based on topicid

  if (!topic) return <div>Topic not found</div>;

  return (
    <div>
      <ResourceNav topics={topicData} currentTopicId={topicid} />
      <h2>{topic.topicid}</h2>
      {topic.sections.map((section, idx) => (
        <div key={idx}>
          <h3>{section.title}</h3>
          <ul>
            {section.qa.map((qa, i) => (
              <li key={i}>
                <p>{qa.question}</p>
                {qa.answerArr.map((answer, j) => (
                  <div key={j}>
                    {answer.type === 'url' ? (
                      <span dangerouslySetInnerHTML={{ __html: answer.key }} />
                    ) : answer.type === 'youtube' ? (
                      <div>
                        <p>{answer.name}</p>
                        <iframe
                          width="560"
                          height="315"
                          src={`https://www.youtube.com/embed/${answer.videoId}`}
                          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                          allowFullScreen
                        ></iframe>
                      </div>
                    ) : null}
                  </div>
                ))}
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}

export default TopicPage;
