import React from 'react';
import { useHistory } from 'react-router-dom';
import topicData from '../../data/topicData.json';

function Resources() {
  const history = useHistory();

  // Function to handle navigation to the topic page
  const navigateToTopic = (topicId) => {
    history.push(`/${topicId}`);
  };

  return (
    <div className="resources">
      {/* Navbar */}
      <nav>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {topicData.map((topic, index) => (
            <li key={index} style={{ display: 'inline-block', marginRight: '10px' }}>
              {/* Render each topic as a button */}
              <button 
                onClick={() => navigateToTopic(topic.topicid)} 
                style={{ textDecoration: 'none', color: 'black', padding: '5px 10px', border: '1px solid black', borderRadius: '5px', background: 'none', cursor: 'pointer' }}
              >
                {topic.topicid}
              </button>
            </li>
          ))}
        </ul>
      </nav>
    </div>
  );
}

export default Resources;



